import React, { Component } from "react";
import "./App.css";
//import React from "react";import { MDBContainer, MDBRow, MDBCol, MDBInput, MDBBtn } from 'mdbreact';


class App extends Component {
  constructor() {
    super();

    this.state = {
      username: "",
      password: "",
      client_id: 2,
      client_secret: "3m6IOBCz2yufSKR4t5ePe9FOwIzVoozut5B2EIat",
      grant_type: "password",
    };
  }
  // fetch api here
  // componentDidMount() {
  //   fetch("http://127.0.0.1:8000/api/test").then(function(response) {
  //     response.json().then(function(resp) {
  //       console.log(resp);
  //     });
  //   });
  // }

  submit() {
    console.log(this.state);

    fetch('http://127.0.0.1:8000/oauth/token', {
      method: 'post',
      body: JSON.stringify(
          this.state
      ),
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
      }
    }).then(function (response) {
      response.json().then(function (resp) {
        console.log(resp)
      })
    })
  }

  render() {
    // return (
    //   <div className="App">
    //     <h1>Test api</h1>
    //     <input type="text" onChange={(item)=>{this.setState({username:item.target.value})}}/>
    //     <input type="password" onChange={(item)=>{this.setState({password:item.target.value})}}/>
    //     <button onClick={()=>{this.submit()}}>Login</button>
    //   </div>
    // );

    return (
        <MDBContainer>
          <MDBRow>
            <MDBCol md="6">
              <form>
                <p className="h5 text-center mb-4">Sign in</p>
                <div className="grey-text">
                  <MDBInput
                      label="Type your email"
                      icon="envelope"
                      group
                      type="email"
                      validate
                      error="wrong"
                      success="right"
                      onChange={(item)=>{this.setState({username:item.target.value})}}
                  />
                  <MDBInput
                      label="Type your password"
                      icon="lock"
                      group
                      type="password"
                      validate
                      onChange={(item)=>{this.setState({password:item.target.value})}}
                  />
                </div>
                <div className="text-center">
                  <MDBBtn onClick={()=>{this.submit()}}>Login</MDBBtn>
                </div>
              </form>
            </MDBCol>
          </MDBRow>
        </MDBContainer>
    );
  }
}

export default App;

